options.css:
.slider-container {
  position: relative;
  max-width: 100%; /* Adjust as needed */
  margin: 0 auto;
  overflow: hidden;
}

.slider {
  display: flex;
  transition: transform 0.5s ease;
}

.slide {
  flex: 0 0 33.33%; /* Each slide occupies 1/3 of the container */
  text-align: center;
  padding: 20px;
}

.slide img {
  max-width: 100%;
  height: auto;
}

.btn {
  display: inline-block;
  padding: 10px 20px;
  background-color: #007bff;
  color: #fff;
  text-decoration: none;
  border-radius: 5px;
  margin-top: 10px;
}

.prev,
.next {
  position: absolute;
  top: 50%;
  transform: translateY(-60%);
  background-color: transparent;
  margin: 10px; 
  border: none;
  color: red;
  font-size: 48px;
  cursor: pointer;
}

.prev {
  left: 10PX;
}

.next {
  right: 10PX;
}
@media (max-width: 800px) {
  .slider {
    flex-wrap: nowrap; /* Prevent slides from wrapping */
  }

  .slide {
    flex: 0 0 100%; /* Each slide will take the full width of the container */
  }
}

options.apply_js:
/*
 * Editor now supports ES6 compliant code. Note that adding ES6
 * specific code to an experiment will break for users running
 * ES5-only browsers as code entered is not transpiled down to ES5.
 */
const utils = window.optimizely.get('utils');
const selector = extension.selector || 'body';
const insertType = extension.insertType; // Ensure this is defined
const html = extension.$html; // Ensure this is defined

 /* utils.waitForElement(selector)
  .then(function(elem) {
    // Prepend the extension html to the body
    elem.insertAdjacentHTML('afterbegin', extension.$html);
    */
    utils.waitForElement(selector).then(function (elem) {
    elem.insertAdjacentHTML(insertType === 'before' ? 'beforebegin' : 'afterend', html);
    const slideContainer = document.getElementById("optimizely-extension-" + extension.$instance);

      // slider code 
    let currentIndex = 0; // Start from the first slide
    const slides = slideContainer.querySelectorAll('.slide');
    const prevButton = slideContainer.querySelector('.prev');
    const nextButton = slideContainer.querySelector('.next');
    const numberOfVisibleSlides = 3; // Number of slides to show at once


    const showSlides = () => {
        // First, hide all slides
        slides.forEach(slide => {
            slide.style.display = 'none';
        });

        // Then, calculate which slides to show
        for (let i = 0; i < numberOfVisibleSlides; i++) {
            let indexToShow = currentIndex + i;

            // Handle wrap-around cases
            if (indexToShow >= slides.length) {
                indexToShow = indexToShow - slides.length;
            } else if (indexToShow < 0) {
                indexToShow = slides.length + indexToShow;
            }

            // Show the slides that need to be visible
            slides[indexToShow].style.display = 'block';
        }
    };

    // Initialize the slider by showing the first set of slides
    showSlides();

    // Event listener for the Previous button
    prevButton.addEventListener('click', () => {
        currentIndex -= 1;
        // Wrap around the slides
        if (currentIndex < 0) {
            currentIndex = slides.length - 1;
        }
        showSlides();
    });

    // Event listener for the Next button
    nextButton.addEventListener('click', () => {
        currentIndex += 1;
        // Wrap around the slides
        if (currentIndex >= slides.length) {
            currentIndex = 0;
        }
        showSlides();
    });

  });

