options.css:
.slider-extention {
 position: relative;
  color: #555;
  font-weight: bold;
  text-align: center;
  font-size: 20px;
}

/* Slider container */
.slider {
    position: relative;
    width: 100%; /* Ensure the slider takes the full width of its parent */
    overflow: hidden; /* Hide slides that overflow the width of the slider */
}

/* Individual slides */
.slide {
    display: none; /* Hide slides by default, will be shown via JavaScript */
    width: 100%; /* Each slide takes full width of the slider */
    height: 37rem; /* Fixed height for each slide */
    /* height: 37.5rem; /* Alternative height in rems */
    background-size: cover; /* Cover the slide area with the background image */
    background-position: center; /* Center the background image */
}

/* Show the first slide by default */
.slide:first-of-type {
    display: block;
}

/* Info box within slides */
.info {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 40%;
    height: 50%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: rgba(240, 240, 240, 0.7);
    padding: 10px;
    border-radius: 10px;
    color: black;
    text-align: center;
}

/* Navigation buttons */
.prev, .next {
    cursor: pointer;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    font-size: 24px;
    color: white;
    background-color: rgba(0,0,0,0.5);
    border: none;
}

.prev {
    left: 10px;
}

.next {
    right: 10px;
}

.button-link {
    display: inline-block;
   background-color: #4CAF50;  Green background
    color: white; /* White text */
    padding: 14px 20px; /* Some padding */
    border: none; /* No border */
    cursor: pointer; /* Add a mouse pointer on hover */
    text-align: center;
    text-decoration: none; /* Remove underline from links */
    font-size: 16px; /* Set a font size */
}

.button-link:hover {
    background-color: #45a049; /* Darker green background on hover */
}


/* Responsive adjustments */
@media (max-width: 768px) {
    .info {
        width: 150px; /* Smaller width on smaller screens */
        padding: 5px;
    }
    .slide {
        height: 400px; /* Adjust slide height for smaller screens */
        /* height: 25rem; /* Alternative height in rems for smaller screens */
    }
}

options.apply_js:
/*
 * Editor now supports ES6 compliant code. Note that adding ES6
 * specific code to an experiment will break for users running
 * ES5-only browsers as code entered is not transpiled down to ES5.
 */

const utils = window.optimizely.get('utils');
const selector = extension.selector || 'body';
const insertType = extension.insert; // Ensure this is defined
const html = extension.$html; // Ensure this is defined
const slideDelayTime = extension.slideDelayTime; 
let slideIndex = 0;
let slideInterval;
let isHovering = false;

utils.waitForElement(selector).then(function (elem) {
    elem.insertAdjacentHTML(insertType === 'before' ? 'beforebegin' : 'afterend', html);
    const slideContainer = document.getElementById("optimizely-extension-" + extension.$instance); // Adjust if needed based on your actual HTML structure
    const slides = slideContainer.querySelectorAll(".slide");
    const prevButton = slideContainer.querySelector('.prev');
    const nextButton = slideContainer.querySelector('.next');

    const showSlides = (isManual = false) => {
        if (!slides.length) return; // Guard clause if no slides are found

        // Only auto-increment slideIndex if not triggered by Prev/Next buttons
        if (!isManual) {
            slideIndex = (slideIndex + 1) % slides.length;
        }

        slides.forEach(slide => slide.style.display = "none");
        slides[slideIndex].style.display = "block";
        resetSlideTimer();
    };

    const resetSlideTimer = () => {
        if (!isHovering) {
            clearTimeout(slideInterval);
            slideInterval = setTimeout(() => showSlides(), slideDelayTime); // Change image every 4 seconds
        }
    };

    prevButton.addEventListener('click', () => {
        slideIndex = slideIndex - 1 < 0 ? slides.length - 1 : slideIndex - 1;
        showSlides(true); // Indicate manual navigation
    });

    nextButton.addEventListener('click', () => {
        // Adjust for manual next navigation
        slideIndex = (slideIndex + 1) % slides.length; // Ensure correct index before showing the slide
        showSlides(true); // Indicate manual navigation
    });

    slideContainer.addEventListener('mouseenter', () => {
        isHovering = true;
        clearTimeout(slideInterval);
    });

    slideContainer.addEventListener('mouseleave', () => {
        isHovering = false;
        resetSlideTimer();
    });

    // Initialize the slideshow
    showSlides();
});

