options.css:
.body1 {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  padding: 20px;
  background-color: #f5f5f5;
}

.card {
  width: 80%; /* Start with a high percentage for mobile devices */
  max-width: 600px; /* Maximum width for larger screens */
  background-color: white;
  border: 1px solid #ddd;
  border-radius: 5px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 15px;
  box-shadow: 0 2px 5px rgba(0,0,0,0.1);
  margin: 10px; 
}

.card img {
  width: 250px;
  height: 250px;
  transition: transform 0.3s ease; /* Smooth transition for the hover effect */
}

.card img:hover {
  transform: scale(1.1); /* Slightly enlarge the images on hover */
}

h3, .card p, .card a {
  width: 100%; /* Ensure elements don't overflow the card */
  text-align: center; /* Center align the text for responsiveness */
}

.card a {
  display: inline-block;
  width: 88%;
  margin-top: 10px;
  padding: 10px 15px;
  background-color: #007bff;
  color: white;
  text-decoration: none;
  border-radius: 5px;
  transition: background-color 0.3s;
}

.card a:hover {
  background-color: #0056b3;
}

.thisparg {
margin-bottom: 95px; 
}

/* Media Queries for Responsive Design */
@media (min-width: 600px) {
  .card {
    width: calc(50% - 20px); /* Adjust width for tablets and above */
  }
}

@media (min-width: 1024px) {
  .card {
    width: calc(33.333% - 20px); /* Adjust width for desktops and larger devices */
  }
}

